import React, { useState, useEffect } from "react";
import Header from '../Components/Header';
import { useRouter } from 'next/router';
import {Card, CardHeader, CardBody, CardFooter, CardTitle, CardText, Container, Row, Col} from "reactstrap"



function Question() {
	const [loading, setLoading] = useState(false);
	const [data, setData] = useState({});
	const router = useRouter()
	const { pid } = router.query


	useEffect(() => {
		fetch(`https://polls.apiblueprint.org/questions/${pid}`).then(response => response.json()).then(data => {
			setData(data)
		});
	}, []);


	return (
		<div>
		<Header/>
		
		<Container>
		  <Row md="2">
			<Col>		
		      <Card>
		        <CardHeader>Header</CardHeader>
		        <CardBody>
		          <CardTitle tag="h5">{data.question}</CardTitle>
		          <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
		        </CardBody>
		        <CardFooter>{data.published_at}</CardFooter>
		      </Card>
		      </Col>
		  </Row>
		</Container>	      
		</div>
	)


} 


export default Question
