import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import "antd/dist/antd.css";

import {
  Form,
  Input,
  Button,
  Radio,
  Select,
  Cascader,
  DatePicker,
  InputNumber,
  TreeSelect,
  Switch,
} from 'antd';

const NewQuestionForm = () => {
  const [componentSize, setComponentSize] = useState('default');

  const onFormLayoutChange = ({ size }) => {
    setComponentSize(size);
  };

        const {getFieldDecorator} = this.props.form;


		  return (
		    
		      <Form
		        labelCol={{
		          span: 4,
		        }}
		        wrapperCol={{
		          span: 14,
		        }}
		        layout="horizontal"
		        initialValues={{
		          size: componentSize,
		        }}
		        onValuesChange={onFormLayoutChange}
		        size={componentSize}
		      >
		        
		        <Form.Item label="question">
		          {getFieldDecorator('question')(<Input/>)}
		        </Form.Item>
		        <Form.Item label="choices">
					{getFieldDecorator('choices')(
					    <Select
					        showSearch
					        placeholder="choices"
					    >
					        <Option value="cash">Peşin</Option>
					        <Option value="installment">Taksit</Option>
					        <Option value="cashandinstallment">Ön Ödeme ve Taksit</Option>
					    </Select>
					)}
		        </Form.Item>
		        <Form.Item label="Create">
		          <Button>Send</Button>
		        </Form.Item>
		      </Form>
		  );
	
};

export default NewQuestionForm;
