import React from 'react';
import ReactDOM from 'react-dom';
import {
    Form,
    Input,
    DatePicker,
    Select,
    Button, message,
} from 'antd';

const {Option} = Select;

class NewQuestionForm extends React.Component {
    state = {
        confirmDirty: false,
        autoCompleteResult: [],
        installment: false,
        cash: false,
        installmentAndCash: false
    };



    handleSubmit = e => {
        e.preventDefault();
        let periods = [];
        let token = localStorage.getItem('token');

        this.props.form.validateFieldsAndScroll((err, values) => {
            if (!err) {
                console.log('Received values of form: ', values);
            }

            periods.push({class: null, status: true, year: values.createdPeriod, payment: values.payment})



            Object.assign(values, {status: true, all_periods: periods});


            fetch("http://localhost:3000/api/student", {
                method: 'post',
                headers: {
                    'Content-Type': 'application/json',
                    'token': token
                },
                // eslint-disable-next-line no-undef
                body: JSON.stringify(values)
            }).then(function (res) {
                return res, res.json()
            })
                .then(function (data) {
                    message
                        .success('Kayıt Başarılı', 1.0)
                        .then(() =>
                            window.location = "/students-list"
                        );

                }).catch(function (err) {
                console.log(err)
            })

        });
    };




    render() {
        const {getFieldDecorator} = this.props.form;

        const formItemLayout = {
            labelCol: {
                xs: {span: 12},
                sm: {span: 8},
                md: {span: 5}
            },
            wrapperCol: {
                xs: {span: 12},
                sm: {span: 16},
                md: {span: 10}
            },
        };
        const tailFormItemLayout = {
            wrapperCol: {
                xs: {
                    span: 12,
                    offset: 0,
                },
                sm: {
                    span: 12,
                    offset: 8,
                },
            },
        };
        const prefixSelector = getFieldDecorator('prefix', {
            initialValue: '90',
        })(
            <Select style={{width: 70}}>
                <Option value="90">+90</Option>
            </Select>,
        );


        return (
              <Form
		        labelCol={{
		          span: 4,
		        }}
		        wrapperCol={{
		          span: 14,
		        }}
		        layout="horizontal"
		        initialValues={{
		          size: componentSize,
		        }}
		        onValuesChange={onFormLayoutChange}
		        size={componentSize}
		      >
		        
		        <Form.Item label="question">
		          {getFieldDecorator('question')(<Input/>)}
		        </Form.Item>
		        <Form.Item label="choices">
					{getFieldDecorator('choices')(
					    <Select
					        showSearch
					        placeholder="choices"
					    >
					        <Option value="cash">Peşin</Option>
					        <Option value="installment">Taksit</Option>
					        <Option value="cashandinstallment">Ön Ödeme ve Taksit</Option>
					    </Select>
					)}
		        </Form.Item>
		        <Form.Item label="Create">
		          <Button>Send</Button>
		        </Form.Item>
		      </Form>
        );
    }
}


const WrappedRegistrationForm = Form.create()(NewQuestionForm);

ReactDOM.render(<WrappedRegistrationForm/>, document.getElementById('root'));

export default WrappedRegistrationForm
