import { useRouter } from 'next/router'
import fetch from 'isomorphic-fetch';
	
const Questions = (props) => {
  const router = useRouter()
  const { pid } = router.query

  return <p>{props.detail.question}</p>
}

Questions.getStaticPaths = async () => {

  return {
    paths: [{ params: { id: '2' } }],
    fallback: true,
  }
};

Questions.getInitialProps = async ({ params }) => {	
	const res = await fetch(`https://polls.apiblueprint.org/${params.id}`);
	const detail = await res.json();

	return {
		detail
	}
};




export default Questions;
