import React, { Component } from 'react'
import {Button, Form, FormGroup, Label, Input} from "reactstrap"
import 'bootstrap/dist/css/bootstrap.min.css';


export default class New extends Component {
	state = {question:'', choice: ''}

	onChangeHandler = (event)=>{
		debugger

		let name = event.target.name;
		let value = event.target.value;
		if (name == "choice") {
			this.setState({[name]: [...this.state.choice, value]})

		} else {			
			this.setState({[name]:value})
		}
	}

	onSubmitHandler = (event)=>{
		event.preventDefault();
		console.log(this.state.question);
		console.log(this.state.choice);
		const sendData = {"question": this.state.question, "choices": [this.state.choice]}
		fetch("https://polls.apiblueprint.org/questions?", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            // eslint-disable-next-line no-undef
            body: JSON.stringify(sendData)
        }).then(function (res) {
            return res, res.json()
        }).then(function (data) {
          		console.log(data);          
            }).catch(function (err) {
            console.log(err)
        })

	}

	
	render() {
		return (
				<div>

					<Form onSubmit={this.onSubmitHandler}>
					   <FormGroup>
						<Label for="question">Question</Label>
						<Input
							name="question"
							type="text"
							placeholder="Enter question" 
							onChange={this.onChangeHandler}
							/>
						</FormGroup>


					   <FormGroup>
						<Label for="choice">Question</Label>
						<Input
							name="choice"
							id="choice"
							type="select"
							multiple
							onChange={this.onChangeHandler}
							>
							<option>a</option>
							<option>b</option>

							</Input>
						</FormGroup>
						
						<Button type="submit">Send</Button>
					</Form>
				</div>
			)
	}
}
